import React, { useState, useEffect } from 'react';
import './Aside.css';
import profileImage from '../assets/usuarios/admin/191.jpg';
import logoClosedImage from '../assets/plantilla/logo11.png';
import logoOpenImage from '../assets/plantilla/logo_final_2.png';

const Tooltip = ({ children, content }) => {
  const [isVisible, setIsVisible] = useState(false);

  return (
    <div 
      className="tooltip-container" 
      onMouseEnter={() => setIsVisible(true)} 
      onMouseLeave={() => setIsVisible(false)}
    >
      {children}
      {isVisible && <div className="tooltip">{content}</div>}
    </div>
  );
};

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isSessionValid, setIsSessionValid] = useState(true);

  const sesionString = localStorage.getItem("session");
  const sesion = sesionString ? JSON.parse(sesionString) : null;

  useEffect(() => {
    if (!sesion || !sesion.user) {
      console.error("No se encontr贸 la sesi贸n o el usuario en localStorage");
      setIsSessionValid(false);
      return;
    }

    const sidebar = document.querySelector(".sidebar");
    const sidebarBtn = document.querySelector(".bx-menu");

    if (sidebarBtn) {
      const handleSidebarToggle = () => {
        sidebar.classList.toggle("close");
      };
      
      sidebarBtn.addEventListener("click", handleSidebarToggle);

      return () => {
        sidebarBtn.removeEventListener("click", handleSidebarToggle);
      };
    }
  }, [sesion]);

  if (!isSessionValid) {
    return null;
  }

  const perfil = sesion.user.perfil;

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const renderMenuItems = () => {
    const menuItems = [];

    if (perfil === "Administrador" || perfil === "Especial" || perfil === "Vendedor") {
      menuItems.push(
        <li key="inicio">
          <Tooltip content="Inicio">
            <a href="inicio">
              <i className="bx bx-home"></i>
              <span className="link_name nombre">Inicio</span>
            </a>
          </Tooltip>
        </li>
      );

      if (perfil === "Administrador") {
        menuItems.push(
          <li key="usuarios">
            <Tooltip content="Usuarios">
              <a href="usuarios">
                <i className="bx bx-user"></i>
                <span className="link_name nombre">Usuarios</span>
              </a>
            </Tooltip>
          </li>
        );
      }

      if (perfil === "Administrador" || perfil === "Especial") {
        menuItems.push(
          <li key="tarifas">
            <Tooltip content="Tarifas">
              <a href="categorias">
                <i className="bx bx-money"></i>
                <span className="link_name nombre">Tarifas</span>
              </a>
            </Tooltip>
          </li>,
          <li key="ingresos">
            <Tooltip content="Ingresos">
              <a href="productos">
                <i className="bx bx-trending-up"></i>
                <span className="link_name nombre">Ingresos</span>
              </a>
            </Tooltip>
          </li>,
          <li key="salidas">
            <Tooltip content="Salidas">
              <a href="salidas">
                <i className="bx bx-exit"></i>
                <span className="link_name nombre">Salidas</span>
              </a>
            </Tooltip>
          </li>
        );
      }

      if (perfil === "Administrador" || perfil === "Vendedor") {
        menuItems.push(
          <li key="abonados">
            <Tooltip content="Abonados">
              <a href="clientes">
                <i className="bx bx-group"></i>
                <span className="link_name nombre">Abonados</span>
              </a>
            </Tooltip>
          </li>,
          <li key="caja">
            <Tooltip content="Caja">
              <a href="cajas">
                <i className="bx bx-cart"></i>
                <span className="link_name nombre">Caja</span>
              </a>
            </Tooltip>
          </li>,
          <li key="pagos">
            <Tooltip content="Pagos">
              <a href="crear-venta">
                <i className="bx bx-dollar"></i>
                <span className="link_name nombre">Pagos</span>
              </a>
            </Tooltip>
          </li>
        );

        if (perfil === "Administrador") {
          menuItems.push(
            <li key="reportes">
              <Tooltip content="Reporte ventas">
                <a href="reportes">
                  <i className="bx bx-line-chart"></i>
                  <span className="link_name nombre">Reporte ventas</span>
                </a>
              </Tooltip>
            </li>
          );
        }
      }
    }

    return menuItems;
  };

  return (
    <>
      <div className={`sidebar ${isOpen ? 'open' : 'close'}`}>
        <div className="logo-details">
          <img
            src={isOpen ? logoOpenImage : logoClosedImage}
            alt="Logo"
            className={`sidebar-logo ${isOpen ? 'logo-open' : 'logo-closed'}`}
          />
        </div>
        <ul className="nav-links">
          {renderMenuItems()}
        </ul>
      </div>
      <section className="home-section">
        <div className={`profile-item ${!isOpen ? 'sidebar-close' : ''}`}>
          <div className="home-content">
            <i className='bx bx-menu' onClick={toggleSidebar}></i>
          </div>
          <div className="profile-details">
            <div className="profile-content">
              <img src={profileImage} alt="profileImg" />
            </div>
            <div className="name-job">
              <div className="profile_name">{sesion.user.nombre}</div>
            </div>
            <i className='bx bx-log-out'></i>
          </div>
        </div>
      </section>
    </>
  );
};

export default Sidebar;
