import React, { useState, useEffect } from 'react';
import './Aside.css';
import profileImage from '../assets/usuarios/admin/191.jpg';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isSessionValid, setIsSessionValid] = useState(true);

  const sesionString = localStorage.getItem("session");
  const sesion = sesionString ? JSON.parse(sesionString) : null;

  useEffect(() => {
    if (!sesion || !sesion.user) {
      console.error("No se encontró la sesión o el usuario en localStorage");
      setIsSessionValid(false);
      return;
    }

    const arrowElements = document.querySelectorAll(".arrow");
    arrowElements.forEach(arrow => {
      arrow.addEventListener("click", (e) => {
        let arrowParent = e.target.parentElement.parentElement; // seleccionando el padre principal de la flecha
        arrowParent.classList.toggle("showMenu");
      });
    });

    const sidebar = document.querySelector(".sidebar");
    const sidebarBtn = document.querySelector(".bx-menu");

    if (sidebarBtn) {
      sidebarBtn.addEventListener("click", () => {
        sidebar.classList.toggle("close");
      });
    }

    // Limpia los event listeners al desmontar el componente
    return () => {
      arrowElements.forEach(arrow => {
        arrow.removeEventListener("click", (e) => {
          let arrowParent = e.target.parentElement.parentElement;
          arrowParent.classList.toggle("showMenu");
        });
      });

      if (sidebarBtn) {
        sidebarBtn.removeEventListener("click", () => {
          sidebar.classList.toggle("close");
        });
      }
    };
  }, [sesion]);

  if (!isSessionValid) {
    return null;
  }

  const perfil = sesion.user.perfil;

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const renderMenuItems = () => {
    let menuItems = [];

    if (perfil === "Administrador" || perfil === "Especial" || perfil === "Vendedor") {
      menuItems.push(
        <li key="inicio">
          <a href="inicio">
            <i className="bx bx-home"></i>
            <span className="link_name nombre">Inicio</span>
          </a>
          <ul className="sub-menu blank">
            <li><a className="link_name nombre" href="inicio">Inicio</a></li>
          </ul>
        </li>
      );

      if (perfil === "Administrador") {
        menuItems.push(
          <li key="usuarios">
            <a href="usuarios">
              <i className="bx bx-user"></i>
              <span className="link_name nombre">Usuarios</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name nombre" href="usuarios">Usuarios</a></li>
            </ul>
          </li>
        );
      }

      if (perfil === "Administrador" || perfil === "Especial") {
        menuItems.push(
          <li key="tarifas">
            <a href="categorias">
              <i className="bx bx-money "></i>
              <span className="link_name nombre">Tarifas</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name nombre" href="categorias">Tarifas</a></li>
            </ul>
          </li>,
          <li key="ingresos">
            <a href="productos">
              <i className="bx bx-trending-up"></i>
              <span className="link_name nombre">Ingresos</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name nombre" href="productos">Ingresos</a></li>
            </ul>
          </li>,
          <li key="salidas">
            <a href="salidas">
              <i className="bx bx-exit"></i>
              <span className="link_name nombre">Salidas</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name nombre" href="salidas">Salidas</a></li>
            </ul>
          </li>
        );
      }

      if (perfil === "Administrador" || perfil === "Vendedor") {
        menuItems.push(
          <li key="abonados">
            <a href="clientes">
              <i className="bx bx-group"></i>
              <span className="link_name nombre">Abonados</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name nombre" href="clientes">Abonados</a></li>
            </ul>
          </li>,
          <li key="caja">
            <a href="cajas">
              <i className="bx bx-cart"></i>
              <span className="link_name nombre">Caja</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name nombre" href="cajas">Caja</a></li>
            </ul>
          </li>,
          <li key="pagos">
            <a href="crear-venta">
              <i className="bx bx-dollar"></i>
              <span className="link_name nombre">Pagos</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name nombre" href="crear-venta">Pagos</a></li>
            </ul>
          </li>
        );

        if (perfil === "Administrador") {
          menuItems.push(
            <li key="reportes">
              <a href="reportes">
                <i className="bx bx-line-chart"></i>
                <span className="link_name nombre">Reporte  ventas</span>
              </a>
              <ul className="sub-menu blank">
                <li><a className="link_name nombre" href="reportes">Reporte de ventas</a></li>
              </ul>
            </li>
          );
        }
      }
    }

    return menuItems;
  };

  return (
    <>
      <div className={`sidebar ${isOpen ? 'open' : 'close'}`}>
        <div className="logo-details">
          <i className='bx bxl-c-plus-plus'></i>
          <span className="logo_name">TuEmpresa</span>
        </div>
        <ul className="nav-links">
          {renderMenuItems()}
        </ul>
      </div>
      <section className="home-section">
        <div className={`profile-item ${!isOpen ? 'sidebar-close' : ''}`}>
          <div className="home-content">
            <i className='bx bx-menu' onClick={toggleSidebar}></i>
          </div>
          <div className="profile-details">
            <div className="profile-content">
              <img src="image/profile.jpg" alt="profileImg" />
            </div>
            <div className="name-job">
              <div className="profile_name">{sesion.user.nombre}</div>
            </div>
            <i className='bx bx-log-out'></i>
          </div>
        </div>
      </section>
    </>
  );
};

export default Sidebar;
