import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import { Image } from 'react-bootstrap';
import Img from '../assets/plantilla/logo11.png';
import Img2 from '../assets/plantilla/logo_final_2.png';
import adminImage from '../assets/usuarios/admin/191.jpg';
import vendedorImage from '../assets/usuarios/juan/461.jpg';
import especialImage from '../assets/usuarios/julio/100.png';
import anonymousImage from '../assets/usuarios/default/anonymous.png';

const HeaderWithSidebar = () => {
  const location = useLocation();
  const [userName, setUserName] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isMenuOpen, setIsMenuOpen] = useState(true);

  useEffect(() => {
    const sesionString = localStorage.getItem("session");
    const sesion = sesionString ? JSON.parse(sesionString) : null;
    if (sesion?.user?.nombre) {
      setUserName(sesion.user.nombre);
    } else {
      setUserName('Usuario');
    }
    setIsLoading(false);
  }, []);

  const getUserImage = () => {
    switch (userName) {
      case 'Administrador': return adminImage;
      case 'Vendedor': return vendedorImage;
      case 'Especial': return especialImage;
      default: return anonymousImage;
    }
  };

  const toggleMenu = (e) => {
    e.preventDefault(); // Previene el comportamiento predeterminado del enlace
    setIsMenuOpen(!isMenuOpen);
  };

  if (isLoading) {
    return <div>Cargando...</div>;
  }

  const renderMenuItems = () => {
    const sesionString = localStorage.getItem("session");
    const sesion = sesionString ? JSON.parse(sesionString) : null;
    const perfil = sesion?.user?.perfil || '';

    let menuItems = [];

    if (perfil === "Administrador" || perfil === "Vendedor" || perfil === "Especial") {
      menuItems.push(
        <li key="inicio">
          <Link to="/inicio">
            <i className="fa fa-home"></i>
            <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Inicio</span>
          </Link>
        </li>
      );
    }

    // Aquí puedes agregar más condiciones para los otros elementos del menú
    // siguiendo el mismo patrón

    return menuItems;
  };

  return (
    <div className={`wrapper ${isMenuOpen ? '' : 'sidebar-collapse'}`}>
      <header className="main-header" style={{ 
        marginLeft: isMenuOpen ? '232px' : '0', 
        transition: 'margin-left 0.3s ease-in-out',
        position: 'fixed',
        width: '100%',
        zIndex: 820
      }}>
        <Link to="/inicio" className="logo" style={{ 
          paddingBottom: '6.4%', 
          backgroundColor: '#000000', 
          borderBottom: '2px solid #e3aa00',
          position: 'fixed',
          zIndex: 830
        }}>
          <span className="logo-mini">
            <Image src={Img} className="img-responsive" style={{ padding: '8px', marginTop: '15%' }} alt="Logo Mini" />
          </span>
          <span className="logo-lg">
            <Image src={Img2} style={{ display: 'block', maxWidth: '88%', height: 'auto', marginTop: '1%' }} alt="Logo Normal" />
          </span>
        </Link>

        <nav className="navbar navbar-static-top" role="navigation" style={{ 
          backgroundColor: '#000000', 
          borderBottom: '2px solid #e3aa00', 
          borderLeft: '2px solid #e3aa00',
          marginLeft: isMenuOpen ? '232px' : '0',
          transition: 'margin-left 0.3s ease-in-out'
        }}>
          <a href="#" className="sidebar-toggle" onClick={toggleMenu}>
            <span className="sr-only">Toggle navigation</span>
          </a>

          <div className="navbar-custom-menu">
            <ul className="nav navbar-nav">
              <li className="dropdown user user-menu">
                <a href="#" className="dropdown-toggle" data-toggle="dropdown" style={{ marginTop: '10%', zoom: '113%' }}>
                  <img src={getUserImage()} className="user-image" alt="User" />
                  <span className="hidden-xs" style={{fontWeight: '500', fontFamily: 'Anton', color:'white', fontSize: '160%'}}>
                    {userName}
                  </span>
                </a>
                <ul className="dropdown-menu">
                  <li className="user-body">
                    <div className="pull-right">
                      <Link to="/salir" className="btn btn-default btn-flat">Salir</Link>
                    </div>
                  </li>
                </ul>
              </li>
            </ul>
          </div>
        </nav>
      </header>

      <aside className="main-sidebar" style={{
        position: 'fixed',
        top: 0,
        left: isMenuOpen ? 0 : '-232px',
        width: '232px',
        height: '100%',
        paddingTop: '78px',
        backgroundColor: '#000000',
        borderRight: '2px solid #e3aa00',
        transition: 'left 0.3s ease-in-out',
        zIndex: 810,
        overflowY: 'auto'
      }}>
        <section className="sidebar">
          <ul className="sidebar-menu">
            {renderMenuItems()}
          </ul>
        </section>
      </aside>

      <div className="content-wrapper" style={{ 
        marginLeft: isMenuOpen ? '232px' : '0', 
        transition: 'margin-left 0.3s ease-in-out',
        paddingTop: '50px'
      }}>
        {/* Aquí iría el contenido principal de la página */}
      </div>
    </div>
  );
};

export default HeaderWithSidebar;
