import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../../public/dist/css/adminlte.css'


const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Lógica para manejar el ingreso del usuario
    console.log('Usuario:', usuario);
    console.log('Contraseña:', password);
    // Aquí podrías agregar la lógica para enviar los datos a tu backend
  };

  return (
    <div id="back">
      <div className="login-box">
        <div className="login-logo">
          <img 
            src="../../src/assets/plantilla/logo_final_2.png" 
            className="img-responsive" 
            style={{ padding: '30px 100px 0px 100px' }} 
            alt="Logo"
          />
        </div>
        <div className="login-box-body">
          <p className="login-box-msg">Ingresar al sistema</p>
          <form onSubmit={handleSubmit}>
            <div className="form-group has-feedback">
              <input 
                type="text" 
                className="form-control" 
                placeholder="Usuario" 
                name="ingUsuario" 
                value={usuario} 
                onChange={(e) => setUsuario(e.target.value)} 
                required 
              />
              <span className="glyphicon glyphicon-user form-control-feedback"></span>
            </div>
            <div className="form-group has-feedback">
              <input 
                type="password" 
                className="form-control" 
                placeholder="Contraseña" 
                name="ingPassword" 
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
                required 
              />
              <span className="glyphicon glyphicon-lock form-control-feedback"></span>
            </div>
            <div className="row">
              <div className="col-xs-4">
                <button type="submit" className="btn btn-primary btn-block btn-flat">
                  Ingresar
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
