import React, { useEffect } from 'react';
import 'boxicons/css/boxicons.min.css';
import './Aside.css';
import { createPopper } from '@popperjs/core';

const Nav = () => {
  const sesionString = localStorage.getItem("session");
  const sesion = sesionString ? JSON.parse(sesionString) : null;

  if (!sesion || !sesion.user) {
    console.error("No se encontró la sesión o el usuario en localStorage");
    return null;
  }

  console.log('Perfil del usuario:', sesion.user.perfil);

  const renderMenuItems = () => {
    const perfil = sesion.user.perfil;

    let menuItems = [];

    if (perfil === "Administrador") {
      menuItems.push(
        <li className="menu-item" key="inicio">
          <a href="inicio">
            <span className="menu-icon">
              <i className="fa fa-home"></i>
            </span>
            <span className="menu-title">Inicio</span>
          </a>
        </li>,
        <li className="menu-item" key="usuarios">
          <a href="usuarios">
            <span className="menu-icon">
              <i className="fa fa-user"></i>
            </span>
            <span className="menu-title">Usuarios</span>
          </a>
        </li>
      );
    }

    if (perfil === "Administrador" || perfil === "Especial") {
      menuItems.push(
        <li className="menu-item" key="categorias">
          <a href="categorias">
            <span className="menu-icon">
              <i className="fa fa-money"></i>
            </span>
            <span className="menu-title">Tarifas</span>
          </a>
        </li>,
        <li className="menu-item" key="productos">
          <a href="productos">
            <span className="menu-icon">
              <i className="fa fa-indent"></i>
            </span>
            <span className="menu-title">Ingresos</span>
          </a>
        </li>,
        <li className="menu-item" key="salidas">
          <a href="salidas">
            <span className="menu-icon">
              <i className="fa fa-sign-out"></i>
            </span>
            <span className="menu-title">Salidas</span>
          </a>
        </li>
      );
    }

    if (perfil === "Administrador" || perfil === "Vendedor") {
      menuItems.push(
        <li className="menu-item" key="clientes">
          <a href="clientes">
            <span className="menu-icon">
              <i className="fa fa-users"></i>
            </span>
            <span className="menu-title">Abonados</span>
          </a>
        </li>
      );
    }

    if (perfil === "Administrador" || perfil === "Vendedor") {
      menuItems.push(
        <li className="menu-item" key="cajas">
          <a href="cajas">
            <span className="menu-icon">
              <i className="fa fa-cart-plus"></i>
            </span>
            <span className="menu-title">Caja</span>
          </a>
        </li>,
        <li className="menu-item" key="crear-venta">
          <a href="crear-venta">
            <span className="menu-icon">
              <i className="fa fa-usd"></i>
            </span>
            <span className="menu-title">Pagos</span>
          </a>
        </li>
      );

      if (perfil === "Administrador") {
        menuItems.push(
          <li className="menu-item" key="reportes">
            <a href="reportes">
              <span className="menu-icon">
                <i className="fa fa-line-chart"></i>
              </span>
              <span className="menu-title">Reporte de ventas</span>
            </a>
          </li>
        );
      }
    }

    return menuItems;
  };

  useEffect(() => {
    const ANIMATION_DURATION = 300;

    const SIDEBAR_EL = document.getElementById("sidebar");
    const SUB_MENU_ELS = document.querySelectorAll(".menu > ul > .menu-item.sub-menu");
    const FIRST_SUB_MENUS_BTN = document.querySelectorAll(".menu > ul > .menu-item.sub-menu > a");
    const INNER_SUB_MENUS_BTN = document.querySelectorAll(".menu > ul > .menu-item.sub-menu .menu-item.sub-menu > a");

    class PopperObject {
      instance = null;
      reference = null;
      popperTarget = null;

      constructor(reference, popperTarget) {
        this.init(reference, popperTarget);
      }

      init(reference, popperTarget) {
        this.reference = reference;
        this.popperTarget = popperTarget;
        this.instance = createPopper(this.reference, this.popperTarget, {
          placement: "right",
          strategy: "fixed",
          resize: true,
          modifiers: [
            {
              name: "computeStyles",
              options: {
                adaptive: false
              }
            },
            {
              name: "flip",
              options: {
                fallbackPlacements: ["left", "right"]
              }
            }
          ]
        });

        document.addEventListener(
          "click",
          (e) => this.clicker(e, this.popperTarget, this.reference),
          false
        );

        const ro = new ResizeObserver(() => {
          this.instance.update();
        });

        ro.observe(this.popperTarget);
        ro.observe(this.reference);
      }

      clicker(event, popperTarget, reference) {
        if (
          SIDEBAR_EL.classList.contains("collapsed") &&
          !popperTarget.contains(event.target) &&
          !reference.contains(event.target)
        ) {
          this.hide();
        }
      }

      hide() {
        this.instance.state.elements.popper.style.visibility = "hidden";
      }
    }

    class Poppers {
      subMenuPoppers = [];

      constructor() {
        this.init();
      }

      init() {
        SUB_MENU_ELS.forEach((element) => {
          this.subMenuPoppers.push(
            new PopperObject(element, element.lastElementChild)
          );
          this.closePoppers();
        });
      }

      togglePopper(target) {
        if (window.getComputedStyle(target).visibility === "hidden")
          target.style.visibility = "visible";
        else target.style.visibility = "hidden";
      }

      updatePoppers() {
        this.subMenuPoppers.forEach((element) => {
          element.instance.state.elements.popper.style.display = "none";
          element.instance.update();
        });
      }

      closePoppers() {
        this.subMenuPoppers.forEach((element) => {
          element.hide();
        });
      }
    }

    const slideUp = (target, duration = ANIMATION_DURATION) => {
      const { parentElement } = target;
      parentElement.classList.remove("open");
      target.style.transitionProperty = "height, margin, padding";
      target.style.transitionDuration = `${duration}ms`;
      target.style.boxSizing = "border-box";
      target.style.height = `${target.offsetHeight}px`;
      target.offsetHeight;
      target.style.overflow = "hidden";
      target.style.height = 0;
      target.style.paddingTop = 0;
      target.style.paddingBottom = 0;
      target.style.marginTop = 0;
      target.style.marginBottom = 0;
      window.setTimeout(() => {
        target.style.display = "none";
        target.style.removeProperty("height");
        target.style.removeProperty("padding-top");
        target.style.removeProperty("padding-bottom");
        target.style.removeProperty("margin-top");
        target.style.removeProperty("margin-bottom");
        target.style.removeProperty("overflow");
        target.style.removeProperty("transition-duration");
        target.style.removeProperty("transition-property");
      }, duration);
    };

    const slideDown = (target, duration = ANIMATION_DURATION) => {
      target.style.removeProperty("display");
      const height = target.offsetHeight;
      target.style.boxSizing = "border-box";
      target.style.overflow = "hidden";
      target.style.height = 0;
      target.style.paddingTop = 0;
      target.style.paddingBottom = 0;
      target.style.marginTop = 0;
      target.style.marginBottom = 0;
      target.offsetHeight;
      target.style.display = null;
      target.style.transitionProperty = "height, margin, padding";
      target.style.transitionDuration = `${duration}ms`;
      target.style.height = `${height}px`;
      target.style.paddingTop = null;
      target.style.paddingBottom = null;
      target.style.marginTop = null;
      target.style.marginBottom = null;
      window.setTimeout(() => {
        target.style.removeProperty("height");
        target.style.removeProperty("overflow");
        target.style.removeProperty("transition-duration");
        target.style.removeProperty("transition-property");
      }, duration);
    };

    const slideToggle = (target, duration = ANIMATION_DURATION) => {
      if (window.getComputedStyle(target).display === "none")
        return slideDown(target, duration);
      return slideUp(target, duration);
    };

    const PoppersInstance = new Poppers();

    const updatePoppersTimeout = () => {
      setTimeout(() => {
        PoppersInstance.updatePoppers();
      }, ANIMATION_DURATION);
    };

    const handleCollapseClick = () => {
      SIDEBAR_EL.classList.toggle("collapsed");
      PoppersInstance.closePoppers();
      if (SIDEBAR_EL.classList.contains("collapsed"))
        FIRST_SUB_MENUS_BTN.forEach((element) => {
          element.parentElement.classList.remove("open");
        });

      updatePoppersTimeout();
    };

    const handleToggleClick = () => {
      SIDEBAR_EL.classList.toggle("toggled");

      updatePoppersTimeout();
    };

    const handleOverlayClick = () => {
      SIDEBAR_EL.classList.toggle("toggled");
    };

    document.getElementById("btn-collapse").addEventListener("click", handleCollapseClick);
    document.getElementById("btn-toggle").addEventListener("click", handleToggleClick);
    document.getElementById("overlay").addEventListener("click", handleOverlayClick);

    const defaultOpenMenus = document.querySelectorAll(".menu-item.sub-menu.open");

    defaultOpenMenus.forEach((element) => {
      element.lastElementChild.style.display = "block";
    });

    FIRST_SUB_MENUS_BTN.forEach((element) => {
      element.addEventListener("click", () => {
        if (SIDEBAR_EL.classList.contains("collapsed"))
          PoppersInstance.togglePopper(element.nextElementSibling);
        else {
          const parentMenu = element.closest(".menu.open-current-submenu");
          if (parentMenu)
            parentMenu
              .querySelectorAll(":scope > ul > .menu-item.sub-menu > a")
              .forEach(
                (el) =>
                  window.getComputedStyle(el.nextElementSibling).display !==
                    "none" && slideUp(el.nextElementSibling)
              );
          slideToggle(element.nextElementSibling);
        }
      });
    });

    INNER_SUB_MENUS_BTN.forEach((element) => {
      element.addEventListener("click", () => {
        slideToggle(element.nextElementSibling);
      });
    });

    return () => {
      document.getElementById("btn-collapse").removeEventListener("click", handleCollapseClick);
      document.getElementById("btn-toggle").removeEventListener("click", handleToggleClick);
      document.getElementById("overlay").removeEventListener("click", handleOverlayClick);
    };
  }, []);

  return (
    <div>
      <aside id="sidebar" className="sidebar">
        <div className="sidebar-header">
          <h3>Menú</h3>
          <div>
            <button id="btn-collapse" className="btn btn-toggle">
              <i className="bx bx-chevron-left"></i>
            </button>
            <button id="btn-toggle" className="btn btn-toggle">
              <i className="bx bx-menu"></i>
            </button>
          </div>
        </div>
        <div className="sidebar-menu">
          <ul className="menu">
            {renderMenuItems()}
          </ul>
        </div>
        <div id="overlay" className="overlay"></div>
      </aside>
    </div>
  );
};

export default Nav;
