import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUser, faMoneyBill, faIndent, faSignOutAlt, faUsers, faCartPlus, faDollarSign, faChartLine } from '@fortawesome/free-solid-svg-icons';

const Sidebar = () => {
  const userProfile = JSON.parse(localStorage.getItem('userProfile')) || { perfil: '' };

  const menuItems = [
    { role: 'Administrador', icon: faHome, text: 'Inicio', link: 'inicio' },
    { role: 'Administrador', icon: faUser, text: 'Usuarios', link: 'usuarios' },
    { role: ['Administrador', 'Especial'], icon: faMoneyBill, text: 'Tarifas', link: 'categorias' },
    { role: ['Administrador', 'Especial'], icon: faIndent, text: 'Ingresos', link: 'productos' },
    { role: ['Administrador', 'Especial'], icon: faSignOutAlt, text: 'Salidas', link: 'salidas' },
    { role: ['Administrador', 'Vendedor'], icon: faUsers, text: 'Abonados', link: 'clientes' },
    { role: ['Administrador', 'Vendedor'], icon: faCartPlus, text: 'Caja', link: 'cajas' },
    { role: ['Administrador', 'Vendedor'], icon: faDollarSign, text: 'Pagos', link: 'crear-venta' },
    { role: 'Administrador', icon: faChartLine, text: 'Reporte de ventas', link: 'reportes' },
  ];

  const renderMenuItems = () => {
    return menuItems.map((item, index) => {
      if (Array.isArray(item.role)) {
        if (item.role.includes(userProfile.perfil)) {
          return (
            <li key={index}>
              <a href={item.link}>
                <FontAwesomeIcon icon={item.icon} />
                <span>{item.text}</span>
              </a>
            </li>
          );
        }
      } else {
        if (item.role === userProfile.perfil) {
          return (
            <li key={index} className={item.text === 'Inicio' ? 'active' : ''}>
              <a href={item.link}>
                <FontAwesomeIcon icon={item.icon} />
                <span>{item.text}</span>
              </a>
            </li>
          );
        }
      }
      return null;
    });
  };

  return (
    <aside className="main-sidebar" style={{ position: 'absolute', top: 0, left: 0, width: '232px', paddingTop: '78px', minHeight: '100%', backgroundColor: '#000000', borderRight: '2px solid #e3aa00' }}>
      <section className="sidebar">
        <ul className="sidebar-menu">
          {renderMenuItems()}
        </ul>
      </section>
    </aside>
  );
};

export default Sidebar;
