import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Image } from 'react-bootstrap';
import Img from '../assets/plantilla/logo11.png';
import Img2 from '../assets/plantilla/logo_final_2.png';
import adminImage from '../assets/usuarios/admin/191.jpg';
import vendedorImage from '../assets/usuarios/juan/461.jpg';
import especialImage from '../assets/usuarios/julio/100.png';
import anonymousImage from '../assets/usuarios/default/anonymous.png';

const HeaderWithSidebar = () => {
  const location = useLocation();
  const [userName, setUserName] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isMenuOpen, setIsMenuOpen] = useState(true);

  useEffect(() => {
    if (location.state?.data?.user?.nombre) {
      setUserName(location.state.data.user.nombre);
    } else {
      setUserName('Usuario');
    }
    setIsLoading(false);
  }, [location.state]);

  const getUserImage = () => {
    switch (userName) {
      case 'Administrador':
        return adminImage;
      case 'Vendedor':
        return vendedorImage;
      case 'Especial':
        return especialImage;
      default:
        return anonymousImage;
    }
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Recuperar y parsear la sesión desde localStorage
  const sesionString = localStorage.getItem("session");
  const sesion = sesionString ? JSON.parse(sesionString) : null;

  if (isLoading || !sesion || !sesion.user) {
    return <div>Cargando...</div>;
  }

  const renderMenuItems = () => {
    const perfil = sesion.user.perfil;
    let menuItems = [];

    if (perfil === "Administrador") {
      menuItems.push(
        <li className="active" key="inicio">
          <a href="inicio">
            <i className="fa fa-home"></i>
            <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Inicio</span>
          </a>
        </li>,
        <li key="usuarios">
          <a href="usuarios">
            <i className="fa fa-user"></i>
            <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Usuarios</span>
          </a>
        </li>
      );
    }

    if (perfil === "Administrador" || perfil === "Especial") {
      menuItems.push(
        <li key="categorias">
          <a href="categorias">
            <i className="fa fa-money"></i>
            <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Tarifas</span>
          </a>
        </li>,
        <li key="productos">
          <a href="productos">
            <i className="fa fa-indent"></i>
            <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Ingresos</span>
          </a>
        </li>,
        <li key="salidas">
          <a href="salidas">
            <i className="fa fa-sign-out"></i>
            <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Salidas</span>
          </a>
        </li>
      );
    }

    if (perfil === "Administrador" || perfil === "Vendedor") {
      menuItems.push(
        <li key="clientes">
          <a href="clientes">
            <i className="fa fa-users"></i>
            <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Abonados</span>
          </a>
        </li>,
        <li key="cajas">
          <a href="cajas">
            <i className="fa fa-cart-plus"></i>
            <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Caja</span>
          </a>
        </li>,
        <li key="crear-venta">
          <a href="crear-venta">
            <i className="fa fa-usd"></i>
            <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Pagos</span>
          </a>
        </li>
      );

      if (perfil === "Administrador") {
        menuItems.push(
          <li key="reportes">
            <a href="reportes">
              <i className="fa fa-line-chart"></i>
              <span style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Reporte de ventas</span>
            </a>
          </li>
        );
      }
    }

    return menuItems;
  };

  return (
    <div className={`wrapper ${isMenuOpen ? '' : 'sidebar-collapse'}`}>
      <header className="main-header" style={{ marginLeft: isMenuOpen ? '232px' : '0', transition: 'margin-left 0.3s ease-in-out' }}>
        <a href="inicio" className="logo" style={{ paddingBottom: '6.4%', backgroundColor: '#000000', borderBottom: '2px solid #e3aa00' }}>
          <span className="logo-mini" style={{ paddingBottom: '0%' }}>
            <Image src={Img} className="img-responsive" style={{ padding: '8px', marginTop: '15%' }} alt="Logo Mini" />
          </span>
          <span className="logo-lg" style={{ paddingBottom: '6.4%' }}>
            <Image src={Img2} style={{ display: 'block', maxWidth: '88%', height: 'auto', marginTop: '1%' }} alt="Logo Normal" />
          </span>
        </a>

        <nav className="navbar navbar-static-top" role="navigation" style={{ backgroundColor: '#000000', borderBottom: '2px solid #e3aa00', borderLeft: '2px solid #e3aa00' }}>
          <a href="#" className="sidebar-toggle" data-toggle="push-menu" role="button" onClick={toggleMenu}>
            <span className="sr-only">Toggle navigation</span>
          </a>

          <div className="navbar-custom-menu">
            <ul className="nav navbar-nav">
              <li className="dropdown user user-menu">
                <a href="#" className="dropdown-toggle" data-toggle="dropdown" style={{ marginTop: '10%', zoom: '113%' }}>
                  <img src={getUserImage()} className="user-image" alt="User" />
                  <span className="hidden-xs" style={{fontWeight: '500', fontFamily: 'Anton', color:'white', fontSize: '160%'}}>
                    {userName || 'Usuario'}
                  </span>
                </a>
                <ul className="dropdown-menu">
                  <li className="user-body">
                    <div className="pull-right">
                      <a href="salir" className="btn btn-default btn-flat">Salir</a>
                    </div>
                  </li>
                </ul>
              </li>
            </ul>
          </div>
        </nav>
      </header>

      <aside className="main-sidebar" style={{
        position: 'fixed',
        top: 0,
        left: isMenuOpen ? 0 : '-232px',
        width: '232px',
        paddingTop: '78px',
        minHeight: '100%',
        backgroundColor: '#000000',
        borderRight: '2px solid #e3aa00',
        transition: 'left 0.3s ease-in-out',
        zIndex: 810
      }}>
        <section className="sidebar">
          <ul className="sidebar-menu">
            {renderMenuItems()}
          </ul>
        </section>
      </aside>

      <div className="content-wrapper" style={{ marginLeft: isMenuOpen ? '232px' : '0', transition: 'margin-left 0.3s ease-in-out' }}>
        {/* Aquí iría el contenido principal de la página */}
      </div>
    </div>
  );
};

export default HeaderWithSidebar;
