import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Image } from 'react-bootstrap';
import Img from "../assets/plantilla/logo11.png";
import Img2 from "../assets/plantilla/logo_final_2.png";
import adminImage from '../assets/usuarios/admin/191.jpg';
import vendedorImage from '../assets/usuarios/juan/461.jpg';
import especialImage from '../assets/usuarios/julio/100.png';
import anonymousImage from '../assets/usuarios/default/anonymous.png';

const Header = () => {
  const location = useLocation();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [userData, setUserData] = useState(null); // Estado para almacenar datos del usuario

  useEffect(() => {
    if (location.state && location.state.data) {
      setUserData(location.state.data);
    }
  }, [location.state]); // Escucha cambios en location.state.data

  const getUserImage = () => {
    if (userData && userData.user) {
      switch (userData.user.nombre) {
        case 'Administrador':
          return adminImage;
        case 'Vendedor':
          return vendedorImage;
        case 'Especial':
          return especialImage;
        default:
          return anonymousImage;
      }
    } else {
      return anonymousImage;
    }
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="main-header" style={{ marginLeft: '0%' }}>
      {/* LOGOTIPO */}
      <a href="inicio" className="logo" style={{ paddingBottom: '6.4%', backgroundColor: '#000000', borderBottom: '2px solid #e3aa00' }}>
        {/* logo mini */}
        <span className="logo-mini" style={{ paddingBottom: '0%' }}>
          <Image src={isMenuOpen ? Img2 : Img} className="img-responsive" style={{ padding: '8px', marginTop: '15%' }} alt="Logo Mini" />
        </span>
        {/* logo normal */}
        <span className="logo-lg" style={{ paddingBottom: '6.4%' }}>
          <Image src={isMenuOpen ? Img2 : Img} style={{ display: 'block', maxWidth: '88%', height: 'auto', marginTop: '1%' }} alt="Logo Normal" />
        </span>
      </a>

      {/* BARRA DE NAVEGACIÓN */}
      <nav className="navbar navbar-static-top" role="navigation" style={{ backgroundColor: '#000000', borderBottom: '2px solid #e3aa00', borderLeft: '2px solid #e3aa00' }}>
        {/* Botón de navegación */}
        <a href="#" className="sidebar-toggle" data-toggle="push-menu" role="button" onClick={toggleMenu}>
          <span className="sr-only">Toggle navigation</span>
        </a>

        {/* perfil de usuario */}
        <div className="navbar-custom-menu">
          <ul className="nav navbar-nav">
            <li className="dropdown user user-menu">
              <a href="#" className="dropdown-toggle" data-toggle="dropdown" style={{ marginTop: '10%', zoom: '113%' }}>
                {/* Imagen de usuario */}
                <img src={userData && userData.user ? getUserImage() : anonymousImage} className="user-image" alt="User" />
                <span className="hidden-xs" style={{ fontWeight: '500', fontFamily: 'Anton', color: 'white', fontSize: '160%' }}>{userData && userData.user ? userData.user.nombre : 'Usuario'}</span>
              </a>
              {/* Dropdown-toggle */}
              <ul className="dropdown-menu">
                <li className="user-body">
                  <div className="pull-right">
                    <a href="salir" className="btn btn-default btn-flat">Salir</a>
                  </div>
                </li>
              </ul>
            </li>
          </ul>
        </div>
      </nav>
    </header>
  );
};

export default Header;
