import React, { useState, useEffect } from 'react';
import './Aside.css';
import profileImage from '../assets/usuarios/admin/191.jpg';
import logoClosedImage from '../assets/plantilla/logo11.png';
import logoOpenImage from '../assets/plantilla/logo_final_2.png';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isSessionValid, setIsSessionValid] = useState(true);

  const sesionString = localStorage.getItem("session");
  const sesion = sesionString ? JSON.parse(sesionString) : null;

  useEffect(() => {
    if (!sesion || !sesion.user) {
      console.error("No se encontr贸 la sesi贸n o el usuario en localStorage");
      setIsSessionValid(false);
      return;
    }

    const sidebar = document.querySelector(".sidebar");
    const sidebarBtn = document.querySelector(".bx-menu");

    if (sidebarBtn) {
      const handleSidebarToggle = () => {
        sidebar.classList.toggle("close");
      };
      
      sidebarBtn.addEventListener("click", handleSidebarToggle);

      return () => {
        sidebarBtn.removeEventListener("click", handleSidebarToggle);
      };
    }
  }, [sesion]);

  if (!isSessionValid) {
    return null;
  }

  const perfil = sesion.user.perfil;

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const renderMenuItems = () => {
    const menuItems = [];

    if (perfil === "Administrador" || perfil === "Especial" || perfil === "Vendedor") {
      menuItems.push({ key: 'inicio', title: 'Inicio', icon: 'bx-home' });

      if (perfil === "Administrador") {
        menuItems.push({ key: 'usuarios', title: 'Usuarios', icon: 'bx-user' });
      }

      if (perfil === "Administrador" || perfil === "Especial") {
        menuItems.push(
          { key: 'tarifas', title: 'Tarifas', icon: 'bx-money' },
          { key: 'ingresos', title: 'Ingresos', icon: 'bx-trending-up' },
          { key: 'salidas', title: 'Salidas', icon: 'bx-exit' }
        );
      }

      if (perfil === "Administrador" || perfil === "Vendedor") {
        menuItems.push(
          { key: 'abonados', title: 'Abonados', icon: 'bx-group' },
          { key: 'caja', title: 'Caja', icon: 'bx-cart' },
          { key: 'pagos', title: 'Pagos', icon: 'bx-dollar' }
        );

        if (perfil === "Administrador") {
          menuItems.push({ key: 'reportes', title: 'Reporte ventas', icon: 'bx-line-chart' });
        }
      }
    }

    return menuItems.map(item => (
      <li key={item.key} title={item.title} className={!isOpen ? 'close' : ''}>
        <div className="icon-container">
          <i className={`bx ${item.icon}`}></i>
          {isOpen && <span className="link_name">{item.title}</span>}
        </div>
        <div className="tooltip-container">
          <span className="tooltip">{item.title}</span>
        </div>
      </li>
    ));
  };

  return (
    <>
      <div className={`sidebar ${isOpen ? 'open' : 'close'}`}>
        <div className="logo-details">
          <img
            src={isOpen ? logoOpenImage : logoClosedImage}
            alt="Logo"
            className={`sidebar-logo ${isOpen ? 'logo-open' : 'logo-closed'}`}
          />
        </div>
        <ul className="nav-links">
          {renderMenuItems()}
        </ul>
      </div>
      <section className="home-section">
        <div className={`profile-item ${!isOpen ? 'sidebar-close' : ''}`}>
          <div className="home-content">
            <i className='bx bx-menu' onClick={toggleSidebar}></i>
          </div>
          <div className="profile-details">
            <div className="profile-content">
              <img src={profileImage} alt="profileImg" />
            </div>
            <div className="name-job">
              <div className="profile_name">{sesion.user.nombre}</div>
            </div>
            <i className='bx bx-log-out'></i>
          </div>
        </div>
      </section>
    </>
  );
};

export default Sidebar;
