import React from 'react';
import { useLocation } from 'react-router-dom';
import '../assets/css/AdminLTE.css';
import '../assets/css/skins/_all-skins.min.css';
import CajasSuperiores from './cajas-superiores';
import GraficoVentas from './reportes/grafico-ventas';
import ProductosMasVendidos from './reportes/ProductosMasVendidos';
import ProductosRecientes from './productos-recientes';
import Header from '../../src/components/Header';
import Aside from '../../src/components/Aside';
import Footer from '../../src/components/Footer';

const Tablero = () => {
  const location = useLocation();
  const data = location.state?.data;

  // Validar si data est치 definido y tiene la estructura esperada
  if (!data || !data.user || !data.user.perfil || !data.user.nombre) {
    // Manejar el caso donde data no est치 definido o no tiene la estructura esperada
    return (
      <div className="content-wrapper">
        <div>Error: Datos del usuario no disponibles.</div>
      </div>
    );
  }

  return (
    <div className="content-wrapper">
      <Header />

      <section className="content-header" style={{ color: 'white', backgroundColor: 'black', marginLeft: '-33%' }}>
        <h1 style={{ fontWeight: '500', fontFamily: 'Anton' }}>
          Tablero
          <small style={{ color: 'white', fontWeight: '500', fontFamily: 'Anton' }}>Panel de Control</small>
        </h1>
        <ol className="breadcrumb">
          <li ><a href="inicio" style={{ color: 'white', fontFamily: 'Anton', fontWeight: '500' }}><i className="fa fa-dashboard" ></i> Inicio</a></li>
          <li className="active" style={{ color: 'white', fontFamily: 'Anton', fontWeight: '500' }}>Tablero</li>
        </ol>
      </section>

      <section className="content">
        <div className="row">
          {data.user.perfil === "Administrador" && (
            <CajasSuperiores />
          )}
        </div>

        <div className="row">
          <div className="col-lg-12">
            {data.user.perfil === "Administrador" && (
              <GraficoVentas />
            )}
          </div>

          <div className="col-lg-6">
            {data.user.perfil === "Administrador" && (
              <ProductosMasVendidos />
            )}
          </div>

          <div className="col-lg-6">
            {data.user.perfil === "Administrador" && (
              <ProductosRecientes />
            )}
          </div>

          <div className="col-lg-12">
            {(data.user.perfil === "Especial" || data.user.perfil === "Vendedor") && (
              <div className="box box-success">
                <div className="box-header">
                  <h1 style={{ fontWeight: '500', fontFamily: 'Anton' }}>Bienvenid@ {data.user.nombre}</h1>
                </div>
              </div>
            )}
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default Tablero;
