import React, { useState, useEffect } from 'react';
import './Aside.css';
import profileImage from '../assets/usuarios/admin/191.jpg';
import logoClosedImage from '../assets/plantilla/logo11.png';
import logoOpenImage from '../assets/plantilla/logo_final_2.png';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(true);
  const [isSessionValid, setIsSessionValid] = useState(true);

  const sesionString = localStorage.getItem("session");
  const sesion = sesionString ? JSON.parse(sesionString) : null;

  useEffect(() => {
    if (!sesion || !sesion.user) {
      console.error("No se encontr贸 la sesi贸n o el usuario en localStorage");
      setIsSessionValid(false);
      return;
    }

    const arrowElements = document.querySelectorAll(".arrow");
    arrowElements.forEach(arrow => {
      arrow.addEventListener("click", (e) => {
        let arrowParent = e.target.parentElement.parentElement;
        arrowParent.classList.toggle("showMenu");
      });
    });

    const sidebar = document.querySelector(".sidebar");
    const sidebarBtn = document.querySelector(".bx-menu");

    if (sidebarBtn) {
      sidebarBtn.addEventListener("click", () => {
        sidebar.classList.toggle("close");
        setIsOpen(!isOpen);
      });
    }

    return () => {
      arrowElements.forEach(arrow => {
        arrow.removeEventListener("click", () => {});
      });

      if (sidebarBtn) {
        sidebarBtn.removeEventListener("click", () => {});
      }
    };
  }, [sesion, isOpen]);

  if (!isSessionValid) {
    return null;
  }

  const perfil = sesion.user.perfil;

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
    document.querySelector(".sidebar").classList.toggle("close");
  };

  const renderMenuItems = () => {
    let menuItems = [];

    if (perfil === "Administrador" || perfil === "Especial" || perfil === "Vendedor") {
      menuItems.push(
        { key: "inicio", title: "Inicio", icon: "bx bx-home", href: "inicio" }
      );

      if (perfil === "Administrador") {
        menuItems.push(
          { key: "usuarios", title: "Usuarios", icon: "bx bx-user", href: "usuarios" }
        );
      }

      if (perfil === "Administrador" || perfil === "Especial") {
        menuItems.push(
          { key: "tarifas", title: "Tarifas", icon: "bx bx-money", href: "categorias" },
          { key: "ingresos", title: "Ingresos", icon: "bx bx-trending-up", href: "productos" },
          { key: "salidas", title: "Salidas", icon: "bx bx-exit", href: "salidas" }
        );
      }

      if (perfil === "Administrador" || perfil === "Vendedor") {
        menuItems.push(
          { key: "abonados", title: "Abonados", icon: "bx bx-group", href: "clientes" },
          { key: "caja", title: "Caja", icon: "bx bx-cart", href: "cajas" },
          { key: "pagos", title: "Pagos", icon: "bx bx-dollar", href: "crear-venta" }
        );

        if (perfil === "Administrador") {
          menuItems.push(
            { key: "reportes", title: "Reporte ventas", icon: "bx bx-line-chart", href: "reportes" }
          );
        }
      }
    }

    return menuItems.map((item) => (
      <li key={item.key}>
        <div className="tooltip-container">
          <a href={item.href}>
            <i className={item.icon}></i>
            <span className="link_name">{item.title}</span>
          </a>
          <span className="tooltip">{item.title}</span>
        </div>
      </li>
    ));
  };

  return (
    <>
      <div className={`sidebar ${isOpen ? '' : 'close'}`}>
        <div className="logo-details">
          <img
            src={isOpen ? logoOpenImage : logoClosedImage}
            alt="Logo"
            className={`sidebar-logo ${isOpen ? 'logo-open' : 'logo-closed'}`}
          />
        </div>
        <ul className="nav-links">
          {renderMenuItems()}
        </ul>
      </div>
      <section className="home-section">
        <div className={`profile-item ${!isOpen ? 'sidebar-close' : ''}`}>
          <div className="home-content">
            <i className='bx bx-menu' onClick={toggleSidebar}></i>
          </div>
          <div className="profile-details">
            <div className="profile-content">
              <img src={profileImage} alt="profileImg" />
            </div>
            <div className="name-job">
              <div className="profile_name">{sesion.user.nombre}</div>
            </div>
            <i className='bx bx-log-out'></i>
          </div>
        </div>
      </section>
    </>
  );
};

export default Sidebar;
