import React, { useState, useEffect, useRef } from 'react';
import './Aside.css';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [showMenus, setShowMenus] = useState({});
  const sidebarRef = useRef(null);
  const sesionString = localStorage.getItem("session");
  const sesion = sesionString ? JSON.parse(sesionString) : null;

  if (!sesion || !sesion.user) {
    console.error("No se encontró la sesión o el usuario en localStorage");
    return null;
  }

  const perfil = sesion.user.perfil;

  useEffect(() => {
    const handleArrowClick = (e) => {
      const arrowParent = e.target.closest('li');
      if (arrowParent) {
        setShowMenus(prev => ({
          ...prev,
          [arrowParent.key]: !prev[arrowParent.key]
        }));
      }
    };

    const arrows = sidebarRef.current.querySelectorAll(".arrow");
    arrows.forEach(arrow => {
      arrow.addEventListener("click", handleArrowClick);
    });

    return () => {
      arrows.forEach(arrow => {
        arrow.removeEventListener("click", handleArrowClick);
      });
    };
  }, []);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const renderMenuItems = () => {
    let menuItems = [];

    if (perfil === "Administrador" || perfil === "Especial" || perfil === "Vendedor") {
      menuItems.push(
        <li key="inicio">
          <a href="inicio">
            <i className="bx bx-home"></i>
            <span className="link_name">Inicio</span>
          </a>
          <ul className="sub-menu blank">
            <li><a className="link_name" href="inicio">Inicio</a></li>
          </ul>
        </li>
      );

      if (perfil === "Administrador") {
        menuItems.push(
          <li key="usuarios">
            <a href="usuarios">
              <i className="bx bx-user"></i>
              <span className="link_name">Usuarios</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name" href="usuarios">Usuarios</a></li>
            </ul>
          </li>
        );
      }

      if (perfil === "Administrador" || perfil === "Especial") {
        menuItems.push(
          <li key="tarifas">
            <a href="categorias">
              <i className="bx bx-money"></i>
              <span className="link_name">Tarifas</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name" href="categorias">Tarifas</a></li>
            </ul>
          </li>,
          <li key="ingresos">
            <a href="productos">
              <i className="bx bx-trending-up"></i>
              <span className="link_name">Ingresos</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name" href="productos">Ingresos</a></li>
            </ul>
          </li>,
          <li key="salidas">
            <a href="salidas">
              <i className="bx bx-exit"></i>
              <span className="link_name">Salidas</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name" href="salidas">Salidas</a></li>
            </ul>
          </li>
        );
      }

      if (perfil === "Administrador" || perfil === "Vendedor") {
        menuItems.push(
          <li key="abonados">
            <a href="clientes">
              <i className="bx bx-group"></i>
              <span className="link_name">Abonados</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name" href="clientes">Abonados</a></li>
            </ul>
          </li>,
          <li key="caja">
            <a href="cajas">
              <i className="bx bx-cart"></i>
              <span className="link_name">Caja</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name" href="cajas">Caja</a></li>
            </ul>
          </li>,
          <li key="pagos">
            <a href="crear-venta">
              <i className="bx bx-dollar"></i>
              <span className="link_name">Pagos</span>
            </a>
            <ul className="sub-menu blank">
              <li><a className="link_name" href="crear-venta">Pagos</a></li>
            </ul>
          </li>
        );

        if (perfil === "Administrador") {
          menuItems.push(
            <li key="reportes">
              <a href="reportes">
                <i className="bx bx-line-chart"></i>
                <span className="link_name">Reporte de ventas</span>
              </a>
              <ul className="sub-menu blank">
                <li><a className="link_name" href="reportes">Reporte de ventas</a></li>
              </ul>
            </li>
          );
        }
      }
    }

    return menuItems;
  };

  return (
    <>
      <div ref={sidebarRef} className={`sidebar ${isOpen ? 'open' : 'close'}`}>
        <div className="logo-details">
          <i className='bx bxl-c-plus-plus'></i>
          <span className="logo_name">TuEmpresa</span>
        </div>
        <ul className="nav-links">
          {renderMenuItems()}
        </ul>
      </div>
      <section className="home-section">
        <div className="home-content">
          <i className='bx bx-menu' onClick={toggleSidebar}></i>
        </div>
      </section>
    </>
  );
};
          {/* <li>
            <div className="profile-details">
              <div className="profile-content">
                <img src="image/profile.jpg" alt="profileImg" />
              </div>
              <div className="name-job">
                <div className="profile_name">{sesion.user.nombre}</div>
                <div className="job">{sesion.user.perfil}</div>
              </div>
              <i className='bx bx-log-out'></i>
            </div>
          </li> */}
    

export default Sidebar;
